import codecs
import click

@click.command()
@click.option(
    "--password", prompt=True, hide_input=True,
    confirmation_prompt=True
)
def encode(password):
    click.echo(f"encoded: {codecs.encode(password, 'rot13')}")
    password = click.prompt("Password", hide_input=True, show_default=False, type=str)
